mapper.select:
/* SQL : com.example.demo.bnInfr.mapper.bnInfrMapper.findAll - 은행정보 */ SELECT BNINFR_NO /* 은행 고유 번호 */ , BNCD /* 은행 코드 */ , BNNM , USEYN , DELYN ,TO_CHAR(REGDAY, 'YYYY-MM-DD HH24:MI:SS') AS REGDAY ,TO_CHAR(MDFCNDAY, 'YYYY-MM-DD HH24:MI:SS') AS MDFCNDAY FROM bnInfr WHERE 1=1
AND BNCD = #{bnCd}
AND BNNM LIKE '%'|| #{bnNm} || '%'

mapper.insert:
INSERT INTO BNINFR /* SQL : com.example.demo.bnInfr.mapper.bnInfrMapper.insert - 은행정보 */
    (
      BNINFR_NO    /* 은행 고유 번호 */
    , BNCD         /* 은행 코드 */
    , BNNM
    , USEYN
    , DELYN
    , REGDAY
    , MDFCNDAY
)
VALUES
    (
    /* 은행 고유 번호 */
  TO_CHAR(SYSTIMESTAMP, 'SSFF3') || LPAD(bnInfr_seq.NEXTVAL, 5, 0)
, #{bnCd}
, #{bnNm}
, #{useYn}
, #{delYn}
, SYSDATE
, SYSDATE )

mapper.update:
/* SQL : com.example.demo.bnInfr.mapper.bnInfrMapper.update - 은행정보 */
UPDATE BNINFR
SET   bncd = #{bncd} /* 은행 코드 */
    , bnnm=#{bnnm}
    , useyn=#{useyn}
    , delyn=#{delyn}
    , mdfcnday=sysdate
WHERE BNINFR_NO=#{bninfr_no}

mapper.delete:
/* SQL : com.example.demo.bnInfr.mapper.bnInfrMapper - 은행정보 */
DELETE FROM BNINFR
/* 은행 고유 번호 */
WHERE BNINFR_NO=#{bninfr_no}
